"use strict";

var iterateObject = require("iterate-object"),
    regexEscape = require("regex-escape");

var DECODE_MAP = require("./character-map");
var ENCODE_MAP = {};

iterateObject(DECODE_MAP, function (value, name) {
    ENCODE_MAP[value] = name;
});

module.exports = {
    /**
     * decode
     * Decodes an encoded string.
     *
     * @name decode
     * @function
     * @param {String} input The encoded string.
     * @returns {String} The decoded string.
     */

    decode: function decode(input) {
        input = input || "";
        var output = input;

        iterateObject(DECODE_MAP, function (value, name) {
            output = output.replace(new RegExp(regexEscape(name), "g"), value);
        });

        return output;
    }

    /**
     * encode
     * Encodes a string.
     *
     * @name encode
     * @function
     * @param {} input The string that must be encoded.
     * @returns {String} The encoded string.
     */
    ,
    encode: function encode(input) {

        input = input || "";
        var output = "";

        for (var i = 0; i < input.length; ++i) {
            var c = input.charAt(i);
            output += ENCODE_MAP[c] || c;
        }

        return output;
    }
};